            */
           }
}];  三．确认升级  [ACOTAManager confirmUpdateWithSubDomain:subDomain deviceId:deviceId newVersion:newVersion callback:^(NSError *error) {
          if(error){
          //返回失败信息，根据error.code做不同的提示或者处理
          }else{
          //确认升级
         }
}];

