        u16PayloadLen = sizeof(ZC_SubDeviceList) + pu8DeviceListInfo- u8ClientNum*sizeof(ZC_SubDeviceInfo);
        AC_BuildMessage(AC_CODE_LIST_SUBDEVICES_RSP,pstruMsg- MsgId,
                        (u8*)pu8DeviceListInfo, u16PayloadLen,
