        /*发送消息*/
        AC_SendMessage(g_u8MsgBuildBuffer, u16DataLen); 
         /*JSON协议内存释放*/
        free(out);
    }

设备接收云端指令可以控制设备，并返回执行结果，其中控制指令和响应指令的消息号必须大于64且小于200。  设备接收云端指令可以查询设备，并返回查询结果，其中查询指令和响应指令的消息号必须大于64且小于200。  设备接收到云端指令必须回响应。  下面的例子是云端下发控制demo灯开关状态，收到指令回应响应。  二进制  参考代码如下：  
    控制数据包=code:68 
    typedef struct tag_STRU_LED_ONOFF
    {       
        u8       u8LedOnOff ; // 0:关，1：开
        u8       u8ControlStatus;//控制消息忽略
        u8       u8Pad[2];       
    }STRU_LED_ONOFF;

    响应数据包=code:102 
    byte0:（1：命令执行成功，0：命令执行错误）

    void AC_DealLed(AC_MessageHead *pstruMsg, AC_OptList *pstruOptList, u8 *pu8Playload)
    {
        u16 u16DataLen;
        u8 resp[4] = {0};

        switch (((STRU_LED_ONOFF *)pu8Playload)- u8LedOnOff)
        {
            case 0://处理开关消息
            case 1:        
                resp[0]=AC_BlinkLed(((STRU_LED_ONOFF *)pu8Playload)- u8LedOnOff);
                break;            

        }
        /*构造消息,接口含义详见下节接口定义*/
        AC_BuildMessage(102,pstruMsg- MsgId,
                        (u8*)resp, sizeof(resp),
                        pstruOptList, 
                        g_u8MsgBuildBuffer,  u16DataLen);
        /*发送消息,接口含义详见下节接口定义*/
        AC_SendMessage(g_u8MsgBuildBuffer, u16DataLen);    
    }  KLV格式  参考代码如下：  控制数据点：key:1 value:int8(0为关闭，1为开启)  控制数据包：code:69  响应数据点：key:1 value:int8(0为关闭，1为开启)      void AC_DealLed(AC_MessageHead *pstruMsg, AC_OptList *pstruOptList, u8 *pu8Playload)
    {
        /*KLV协议内存申请,接口含义详见下节接口定义*/
        AC_KLV *pOut = AC_CreateObj();
        u8 u8LedOnOff= 0;
        u16 u16length = 0;
        u8 u8Type = 0;
        AC_GetKeyValue(pu8Playload,AC_HtoNs(pstruMsg- Payloadlen),1, u8LedOnOff, u16length, u8Type);
        switch (u8LedOnOff)
        {
            case 0://处理开关消息
            case 1:        
                AC_BlinkLed(u8LedOnOff);
                break;            

        }
        /*构造KLV消息,接口含义详见下节接口定义*/
        AC_SetKeyValue(pOut,KEY_LED_ON_OFF,sizeof(u8LedOnOff),INT8_TYPE, u8LedOnOff);
        /*发送响应消息，用户自定义,接口含义详见下节接口定义*/
        AC_SendKLVMessage(pstruMsg, pstruOptList,pOut);
         /*KLV协议内存释放,接口含义详见下节接口定义*/
        AC_FreeObj(pOut);    
    }  JSON格式  参考代码如下：  控制数据包= code:70 + req:{"switch",1}   响应数据包= code:102 +resp:{"result",1}      void AC_DealJsonMessage(AC_MessageHead *pstruMsg, AC_OptList *pstruOptList, u8 *pu8Playload)
    {   
        /*处理设备自定义控制消息*/
         u16 u16DataLen;
         u32 u32LedOnOff;
         bool result = false;
         char *out;
         cJSON *root;
        /*解析收到的JSON数据*/
        cJSON *format = cJSON_Parse(pu8Playload);

        if(format)
        {
            u32LedOnOff = cJSON_GetObjectItem(format, switch )- valueint;
            switch (u32LedOnOff)
            {
                case 0://处理开关消息
                case 1:        
                result = AC_BlinkLed(u32LedOnOff);
                break;
            }
            cJSON_Delete(format);
        }
        /*JSON内存申请/*
        root=cJSON_CreateObject();
        /*构造JSON消息*/
        cJSON_AddBoolToObject(root, result ,result);
        out=cJSON_Print(root);  
        cJSON_Delete(root);
        /*发送JSON消息,接口含义详见下节接口定义*/
        AC_BuildMessage(102,0,
                        (u8*)out, strlen(out),
                        NULL, 
                        g_u8MsgBuildBuffer,  u16DataLen);
        AC_SendMessage(g_u8MsgBuildBuffer, u16DataLen); 
        /*释放JSON消息,接口含义详见下节接口定义*/
        free(out);   
     }

安卓设备开发指导


开发准备


SDK发布库


AbleCloud发布的安卓设备SDK为
ac_device_android.jar
，除此之外，还需要导入
libDevice-Service.so
文件（可根据不同cpu做不同选择）




具体步骤
:把文件拷入你自己的工程的libs目录下并设置依赖。




开发环境设置


以下为 AbleCloud Android SDK 需要的所有的权限，请在你的AndroidManifest.xml文件里的
manifest
标签里添加


uses-permission android:name=
android.permission.INTERNET
/


